SELECT goods_id ,goods_code ,title ,desc1 ,area_id ,online_date ,offline_date ,label ,label_promotion ,category ,data_source ,data_source_type ,display_area ,contact_person ,contact_tel ,pic_info ,pic_detail_info ,specification ,agent ,pack ,IFNULL(cost_price,0) AS cost_price ,IFNULL(market_price,0) AS market_price ,IFNULL(taobao_price ,0) AS taobao_price ,IFNULL(discount_price ,0) AS discount_price ,IFNULL(ladder_price,0) AS ladder_price ,ts_min_num ,ts_price ,ts_date ,cost_allocation ,product_source ,shipping_fee ,settled_price ,service_fee ,producer ,supplier_id ,supplier ,warehouse ,warehouse_id ,manufacturer ,min_buy_qty ,max_buy_qty ,sale_qty ,stock_qty ,goods_unit ,delivery_area ,valid_thru ,delivery ,delivery_id ,payment_way ,status ,created_date ,modified_date ,remark ,data_source_type ,voucher_link ,discount_end ,taobao_link ,taobao_sales ,huiguo_commission ,huiguo_vip ,self_label ,commission ,flags FROM ps_goods
status <> 'delete'
and flags=#{flags}
and (flags IS NULL OR flags ='0')
and top_catid =#{top_catid}
and two_catid =#{two_catid}
and three_catid =#{three_catid}
AND goods_id = #{goods_id}
AND goods_code = #{goods_code}
AND goods_code in
#{item}
AND category = #{category}
AND supplier_id = #{supplier_id}
AND category in
#{item}
AND status = #{status}
AND data_source in
#{item}
AND data_source_type = #{data_source_type}
AND status in
#{item}
AND display_area like CONCAT('%',#{display_area},'%')
AND
display_area like CONCAT('%',#{item},'%')
AND
delivery_area = #{item}
AND (title like CONCAT('%',#{goods_like},'%') or goods_code like CONCAT('%',#{goods_like},'%') or contact_person like CONCAT('%',#{goods_like},'%') or contact_tel like CONCAT('%',#{goods_like},'%') or manufacturer like CONCAT('%',#{goods_like},'%') or supplier like CONCAT('%',#{goods_like},'%'))
AND label_promotion like CONCAT('%',#{label_promotion},'%')
AND DATE_FORMAT(created_date, '%Y-%m-%d') >= DATE_FORMAT(#{created_date_start}, '%Y-%m-%d')
AND DATE_FORMAT(created_date, '%Y-%m-%d') <= DATE_FORMAT(#{created_date_end}, '%Y-%m-%d')
AND valid_thru >= #{valid_thru_start}
AND valid_thru <= #{valid_thru_end}
ORDER BY modified_date DESC

