SELECT gd.*, CASE WHEN gd.nowdiffOffline_datePro > 7 THEN '7+' WHEN gd.nowdiffOffline_datePro <= 7 AND gd.nowdiffOffline_datePro > 1 THEN gd.nowdiffOffline_datePro WHEN gd.nowdiffOffline_datePro >= 0 AND gd.nowdiffOffline_datePro <= 1 THEN '1' END AS nowdiffOffline_date
FROM ( SELECT goods_id, goods_code, title, desc1, goods_attr, cat_topid, cat_twoid, cat_threeid, cat_topname, cat_twoname, cat_threename, brand_id, brand_name, online_date, offline_date, label, label_promotion, category, display_area, contact_person, contact_tel, pic_info, pic_detail_info, specification, pack, cost_price, market_price, discount_price, ladder_price, ts_min_num, ts_price, ts_date, cost_allocation, product_source, shipping_fee, settled_price, service_fee, producer, manufacturer, sale_qty, stock_qty, goods_unit, valid_thru, payment_way, status, created_date, modified_date, remark, parity_price, parity_platform, parity_url, commission_fee, vip_fee, total_sales, to_days(now()) - to_days(offline_date) 'nowdiffOffline_datePro' FROM gd_goods_item) AS gd
gd.status <> 'delete'
AND gd.goods_id = #{goods_id}
AND gd.goods_code = #{goods_code}
AND gd.category = #{category}
AND gd.status in
#{item}
AND gd.display_area like CONCAT('%',#{display_area},'%')
AND
gd.display_area like CONCAT('%',#{item},'%')
AND (gd.title like CONCAT('%',#{goods_like},'%') or gd.goods_code like CONCAT('%',#{goods_like},'%') or gd.contact_person like CONCAT('%',#{goods_like},'%') or gd.contact_tel like CONCAT('%',#{goods_like},'%') or gd.manufacturer like CONCAT('%',#{goods_like},'%') or gd.supplier like CONCAT('%',#{goods_like},'%'))
AND gd.label_promotion like CONCAT('%',#{label_promotion},'%')
AND gd.DATE_FORMAT(created_date, '%Y-%m-%d') >= DATE_FORMAT(#{created_date_start}, '%Y-%m-%d')
AND gd.DATE_FORMAT(created_date, '%Y-%m-%d') <= DATE_FORMAT(#{created_date_end}, '%Y-%m-%d')
AND gd.valid_thru >= #{valid_thru_start}
AND gd.valid_thru <= #{valid_thru_end}
AND gd.total_sales <= #{total_sales}
ORDER BY gd.modified_date DESC

