AND category in
#{item}
AND display_area like CONCAT('%',#{display_area},'%')
AND
display_area like CONCAT('%',#{item},'%')
AND
delivery_area = #{item}
AND (title like CONCAT('%',#{goods_like},'%') or goods_code like CONCAT('%',#{goods_like},'%'))
AND label_promotion like CONCAT('%',#{label_promotion},'%')
