select T.goods_id ,T.goods_code ,T.title ,T.desc1 ,T.top_catid ,T.two_catid ,T.three_catid ,T.label ,T.label_promotion ,T.goods_source ,T.display_area ,T.contact_person ,T.contact_tel ,T.pic_info ,T.pic_detail_info ,T.specification ,T.pack ,T.market_price ,T.sale_price ,T.beike_credit ,T.vip_price ,T.shipping_fee ,T.settled_price ,T.producer ,T.supplier_id ,T.supplier ,T.manufacturer ,T.min_buy_qty ,T.max_buy_qty ,T.sale_qty ,T.stock_qty ,T.goods_unit ,T.valid_thru ,T.warehouse ,T.warehouse_id ,T.delivery ,T.delivery_id ,T.delivery_area ,T.payment_way ,T.status ,T.created_date ,T.modified_date ,T.remark ,T.online_date ,T.fake_sales_volume ,T.offline_date FROM beike_mall_goods T
1=1
AND T.label_promotion = #{label_promotion}
AND T.online_date >= #{online_date_start}
AND T.valid_thru > #{valid_thru_start}
AND T.valid_thru < #{valid_thru_end}
AND goods_id = #{goods_id}
AND goods_code = #{goods_code}
AND status = #{status}
AND (title like CONCAT('%',#{goods_like},'%') or desc1 like CONCAT('%',#{goods_like},'%'))
ORDER BY sorted DESC,online_date DESC

mapper.update:
UPDATE
beike_mall_goods SET
sale_qty = sale_qty+#{restore_qty}
WHERE
goods_id=#{goods_id}

mapper.select:
select  FROM beike_mall_goods

	status <> 'delete'

		and top_catid =#{top_catid}


		and two_catid =#{two_catid}


		and three_catid =#{three_catid}


		AND goods_id = #{goods_id}


		AND goods_code = #{goods_code}


		AND goods_code in

			#{item}



		AND supplier_id = #{supplier_id}


		AND status = #{status}



		AND goods_source in

			#{item}



		AND date_source_type = #{date_source_type}



		AND status in

			#{item}



		AND display_area like CONCAT('%',#{display_area},'%')


		AND

			display_area like
			CONCAT('%',#{item},'%')



		AND

			delivery_area = #{item}



		AND (title like CONCAT('%',#{goods_like},'%') or
		goods_code like CONCAT('%',#{goods_like},'%') or
		contact_person like
		CONCAT('%',#{goods_like},'%') or
		contact_tel like
		CONCAT('%',#{goods_like},'%') or
		manufacturer like
		CONCAT('%',#{goods_like},'%') or
		supplier like
		CONCAT('%',#{goods_like},'%'))


		AND label_promotion like
		CONCAT('%',#{label_promotion},'%')


		AND DATE_FORMAT(created_date, '%Y-%m-%d') >=
		DATE_FORMAT(#{created_date_start}, '%Y-%m-%d')


		AND DATE_FORMAT(created_date, '%Y-%m-%d') <=
		DATE_FORMAT(#{created_date_end}, '%Y-%m-%d')


		AND valid_thru >= #{valid_thru_start}


		AND valid_thru <= #{valid_thru_end}


ORDER BY modified_date DESC
