mapper.select:
SELECT order_id ,order_no ,order_date ,loaded_code ,stores_id ,stores_name ,consumer_id ,desc1 ,item_num ,payment_way_key ,total_fee ,delivery_address ,contact_person ,contact_tel ,logistics ,status ,created_date ,remark ,modified_date ,delivery_date FROM pc_order
1=1
AND order_id = #{order_id}
AND order_no = #{order_no}
AND stores_id = #{stores_id}
AND loaded_code = #{loaded_code}
AND stores_id in
#{item}
AND consumer_id = #{consumer_id}
AND DATE_FORMAT(order_date, '%Y-%m-%d') = DATE_FORMAT(#{order_date}, '%Y-%m-%d')
AND order_date >= #{order_date_start}
AND order_date <= date_add(#{order_date_end},interval 1 day)
AND (order_no like CONCAT('%',#{order_like},'%') or contact_person like CONCAT('%',#{order_like},'%') or contact_tel like CONCAT('%',#{order_like},'%') or logistics like CONCAT('%',#{order_like},'%') )
AND status = #{status}
AND status in
#{item}
ORDER BY order_date DESC

SELECT order_item_id ,order_id ,item_store_id ,item_name ,image_info ,qty ,weight ,specification ,sale_price ,created_date ,modified_date ,remark FROM pc_order_item
1=1
AND order_id = #{order_id}

SELECT count(order_id) as count_num FROM pc_order
status = 'payed'
AND stores_id = #{stores_id}


