SELECT a.row_no ,a.group_no ,CASE when a.group_no = 1 THEN a.order_id ELSE NULL END order_id ,CASE WHEN a.group_no = 1 THEN a.order_no ELSE NULL END order_no ,CASE WHEN a.group_no = 1 THEN a.order_date ELSE NULL END order_date ,CASE WHEN a.group_no = 1 THEN a.stores_id ELSE NULL END stores_id ,CASE WHEN a.group_no = 1 THEN a.stores_name ELSE NULL END stores_name ,CASE WHEN a.group_no = 1 THEN a.item_num ELSE NULL END item_num ,CASE WHEN a.group_no = 1 THEN a.total_fee ELSE NULL END total_fee ,CASE WHEN a.group_no = 1 THEN a.delivery_address ELSE NULL END delivery_address ,CASE WHEN a.group_no = 1 THEN a.contact_person ELSE NULL END contact_person ,CASE WHEN a.group_no = 1 THEN a.contact_tel ELSE NULL END contact_tel ,CASE WHEN a.group_no = 1 THEN a.logistics ELSE NULL END logistics ,CASE WHEN a.group_no = 1 THEN a.status ELSE NULL END status ,CASE WHEN a.group_no = 1 THEN a.remark ELSE NULL END remark ,a.delivery_date ,a.item_store_id ,a.item_name ,a.qty item_qty ,a.discount_fee item_discount_fee ,a.specification item_specification FROM ( SELECT @row_no := @row_no + 1 AS row_no, @group_no := CASE WHEN @order_id = a.order_id THEN @group_no + 1 ELSE 1 END AS group_no, @order_id := a.order_id as order_id, a.order_no,a.order_date,a.stores_id,a.stores_name,a.item_num,a.total_fee,a.delivery_address,a.contact_person,a.contact_tel,a.logistics,a.status,a.remark, a.delivery_date,a.item_store_id, a.item_name,a.qty,a.discount_fee, a.specification FROM ( SELECT a.order_id,a.order_no,a.order_date,a.stores_id,a.stores_name,a.item_num,a.total_fee,a.delivery_address,a.contact_person,a.contact_tel, a.logistics,a.status,a.remark,a.delivery_date,b.item_store_id, b.item_name,b.qty,b.discount_fee,b.specification FROM pc_order a INNER JOIN pc_order_item b ON a.order_id = b.order_id
AND a.order_no = #{order_no}
AND status = #{status}
AND a.status in
#{item}
AND a.stores_id in
#{item}
AND a.contact_tel = #{contact_tel}
AND a.logistics_like LIKE CONCAT('%',#{logistics_like},'%')
AND a.order_date >= #{order_date_start}
AND a.order_date <= date_add(#{order_date_end},interval 1 day)
ORDER BY a.order_date DESC, a.order_id ) a, (SELECT @group_no:=1, @order_id:='') b,(SELECT @row_no := 0) c ) a ORDER BY a.row_no ASC

SELECT a.order_id ,a.order_no ,a.order_date ,a.loaded_code ,a.stores_id ,a.stores_name ,a.consumer_id ,a.desc1 ,a.item_num ,a.payment_way_key ,a.total_fee ,a.delivery_address ,a.contact_person ,a.contact_tel ,a.logistics ,a.status ,a.created_date ,a.remark ,a.modified_date ,a.delivery_date ,b.item_store_id ,b.item_name ,b.image_info FROM pc_order a INNER JOIN pc_order_item b ON a.order_id = b.order_id
1=1
AND a.order_id = #{order_id}
AND a.order_no = #{order_no}
AND a.stores_id = #{stores_id}
AND a.loaded_code = #{loaded_code}
AND a.stores_id in
#{item}
AND a.consumer_id = #{consumer_id}
AND DATE_FORMAT(a.order_date, '%Y-%m-%d') = DATE_FORMAT(#{order_date}, '%Y-%m-%d')
AND a.order_date >= #{order_date_start}
AND a.order_date <= date_add(#{order_date_end},interval 1 day)
AND (a.order_no like CONCAT('%',#{order_like},'%') or a.contact_person like CONCAT('%',#{order_like},'%') or a.contact_tel like CONCAT('%',#{order_like},'%') or a.logistics like CONCAT('%',#{order_like},'%') )
AND a.status = #{status}
AND a.status in
#{item}
AND a.order_id in
#{item}
ORDER BY order_date DESC
