mapper.select:
SELECT order_id ,order_no ,order_date ,member_id ,member_info ,desc1 ,item_num ,payment_way_key ,cash_amount ,gold_amount ,delivery_address ,contact_person ,contact_tel ,logistics ,status ,settle_status ,created_date ,modified_date ,remark ,biz_remark FROM ge_order
1=1
AND order_id = #{order_id}
AND order_no = #{order_no}
AND status = #{status}
AND settle_status = #{settle_status}
AND member_id = #{member_id}
AND DATE_FORMAT(order_date, '%Y-%m-%d') = DATE_FORMAT(#{order_date}, '%Y-%m-%d')
AND order_date >= #{order_date_start}
AND order_date <= date_add(#{order_date_end},interval 1 day)
AND (order_no like CONCAT('%',#{order_like},'%') or desc1 like CONCAT('%',#{order_like},'%') or logistics like CONCAT('%',#{order_like},'%') or member_info like CONCAT('%',#{order_like},'%') or contact_person like CONCAT('%',#{order_like},'%') or contact_tel like CONCAT('%',#{order_like},'%'))
ORDER BY order_date DESC

SELECT o.order_id ,o.order_no ,o.order_date ,o.member_id ,o.member_info ,o.desc1 ,o.item_num ,o.payment_way_key ,o.cash_amount ,o.gold_amount ,o.delivery_address ,o.contact_person ,o.contact_tel ,o.logistics ,o.status ,o.settle_status ,o.remark ,o.biz_remark ,oi.goods_code item_goods_code ,oi.goods_title item_goods_title ,oi.supplier item_supplier FROM ge_order o inner join ge_order_item oi ON o.order_id = oi.order_id
AND order_no = #{order_no}
AND status = #{status}
AND settle_status = #{settle_status}
AND oi.supplier_id = #{supplier_id}
AND DATE_FORMAT(order_date, '%Y-%m-%d') = DATE_FORMAT(#{order_date}, '%Y-%m-%d')
AND order_date >= #{order_date_start}
AND order_date <= date_add(#{order_date_end},interval 1 day)
AND (order_no like CONCAT('%',#{order_like},'%') or desc1 like CONCAT('%',#{order_like},'%') or logistics like CONCAT('%',#{order_like},'%') or member_info like CONCAT('%',#{order_like},'%') or contact_person like CONCAT('%',#{order_like},'%') or contact_tel like CONCAT('%',#{order_like},'%'))
ORDER BY o.order_date DESC

SELECT order_id ,goods_id ,goods_code ,goods_title ,goods_pic_info ,qty ,specification ,goods_unit ,retail_price ,discount_price ,cash_paid ,gold_paid FROM ge_order_item
1=1
AND order_id = #{order_id}

SELECT sum(cash_amount) as cash_total_fee ,sum(gold_amount) as gold_total_fee ,count(order_id) as count_num FROM ge_order
AND status = #{status}
AND settle_status = #{settle_status}
AND DATE_FORMAT(order_date, '%Y-%m-%d') = DATE_FORMAT(#{order_date}, '%Y-%m-%d')
AND (order_no like CONCAT('%',#{order_like},'%') or desc1 like CONCAT('%',#{order_like},'%') or logistics like CONCAT('%',#{order_like},'%') or member_info like CONCAT('%',#{order_like},'%') or contact_person like CONCAT('%',#{order_like},'%') or contact_tel like CONCAT('%',#{order_like},'%'))

SELECT o.order_id ,o.order_no ,o.order_date ,o.modified_date ,oi.goods_id item_goods_id ,oi.goods_code item_goods_code ,oi.goods_title item_goods_title ,oi.supplier_id item_supplier_id ,oi.supplier item_supplier ,oi.qty item_qty ,oi.settled_price item_settled_price FROM ge_order o INNER JOIN ge_order_item oi ON o.order_id = oi.order_id
AND o.status = #{status}
AND o.settle_status = #{settle_status}
AND DATE_FORMAT(o.order_date, '%Y-%m-%d') = DATE_FORMAT(#{order_date}, '%Y-%m-%d')
