mapper.select:

	SELECT
		o.order_id
		,o.order_no
		,o.order_type
		,o.total_fee
		,o.discount_fee
		,o.sale_fee
		,o.member_type_key
		,o.member_id
		,o.member_info
		,o.member_mobile
		,o.logistics
		,o.modified_date
		,o.status
		,o.settle_status
	FROM
		hg_order o


	1=1

		AND o.order_type = #{order_type}


		AND o.order_id = #{order_id}


		AND o.order_no = #{order_no}


		AND o.member_id = #{member_id}


		AND o.member_type_key = #{member_type_key}


		AND o.member_mobile = #{member_mobile}


		AND o.member_info like
		CONCAT('%',#{member_info_like},'%')


		AND o.status = #{status}


		AND o.status <> #{status_neq}


		AND o.external_order_no = #{external_order_no}


		AND o.order_date >= #{order_date_start}


		AND o.order_date <=
		date_add(#{order_date_end},interval 1 day)


ORDER BY o.order_date DESC


