SELECT o.order_id ,o.order_no ,o.order_date ,o.order_type ,o.desc1 ,o.payment_way_key ,o.total_fee ,o.discount_fee ,o.sale_fee ,o.member_type_key ,o.member_id ,o.member_info ,o.member_mobile ,o.delivery_address ,o.contact_person ,o.contact_tel ,o.logistics ,o.data_source ,o.external_order_no ,o.modified_date ,o.remark ,o.biz_remark ,o.status ,o.settle_status ,o.transaction_no ,o.qty ,oi.goods_id as item_goods_id ,oi.goods_code as item_goods_code ,oi.goods_title as item_goods_title ,oi.specification as item_specification ,oi.manufacturer as item_manufacturer ,oi.supplier_name as item_supplier ,oi.qty as item_qty ,oi.goods_unit as item_goods_unit ,oi.sale_price as item_sale_price ,oi.total_fee as item_total_fee ,oi.discount_fee as item_discount_fee ,oi.settled_price as item_settled_price ,oi.service_fee as item_service_fee ,oi.remark as item_remark FROM hg_order o inner join hg_order_item oi on oi.order_id = o.order_id
1=1
AND o.order_type = #{order_type}
AND o.member_id = #{member_id}
AND o.member_type_key = #{member_type_key}
AND o.order_id = #{order_id}
AND o.order_no = #{order_no}
AND o.contact_tel = #{contact_tel}
AND o.status = #{status}
AND o.status <> #{status_neq}
AND o.settle_status = #{settle_status}
AND o.status in
#{item}
AND o.logistics LIKE CONCAT('%',#{logistics_like},'%')
AND o.logistics IS NOT NULL
AND oi.goods_id = #{goods_id}
AND oi.goods_code = #{goods_code}
AND oi.goods_title LIKE CONCAT('%',#{goods_title_like},'%')
AND oi.supplier_id = #{supplier_id}
AND oi.supplier_name LIKE CONCAT('%',#{supplier_like},'%')
AND oi.manufacturer LIKE CONCAT('%',#{manufacturer_like},'%')
AND (o.order_no like CONCAT('%',#{order_like},'%') or o.external_order_no like CONCAT('%',#{order_like},'%') or o.contact_tel like CONCAT('%',#{order_like},'%') or oi.goods_code like CONCAT('%',#{order_like},'%'))
AND o.order_date >= #{order_date_start}
AND o.order_date <= date_add(#{order_date_end},interval 1 day)
AND DATE_FORMAT(o.order_date, '%Y-%m-%d') = DATE_FORMAT(#{order_date}, '%Y-%m-%d')
