mapper.select:
	  select 

  from t_comment
  where 1=1

	and DATE_FORMAT(o.create_time,'%Y-%m-%d')  >=  #{startTime,jdbcType=DATE}


	and #{endTime,jdbcType=DATE}  >=  DATE_FORMAT(o.create_time,'%Y-%m-%d')


  and comment_type=#{commentType}


  and goods_name=#{goodsName} 
  or order_no=#{goodsName} 
  or customer_phone=#{goodsName} 
  or customer_name=#{goodsName}

  order by create_time desc
